pragma solidity ^0.6.0;
import "../node_modules/@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "../node_modules/@openzeppelin/contracts/access/Ownable.sol";
// import "./ERC20.sol";
import "./ERC_777.sol";

contract CardsMarket is ERC721, Ownable{
    constructor() ERC721("NFT","NFT") public { }
    struct Cards {
        string name;
        int aggressivity;
        int defense;
        uint quantity;
        string description;
        uint  Object_of_action;
        int aggressivity_action;
        int defense_action;
        uint tokenid;
    }
    // mapping (address => uint256) public balanceOf;
    string[24] public card_names = ["","","","","","","","","","","","","","","","","","","","","","","",""];
    int[24] public card_aggressivity = [9,2,3,3,2,1,3,8,1,3,5,3,4,6,2,5,1,4,3,5,2,0,7,6];
    int[24] public card_defense = [7,5,4,6,5,3,6,7,5,4,4,2,3,6,3,5,7,1,3,2,2,3,4,6];
    // string[24] public description = [];
    
    Cards[] public All_Cards;
    mapping(uint256 => Cards) public map_cards;
    function Create_The_Card (string memory _name, int _aggressivity, int _defense, uint _quantity,string memory _description, uint _Object_of_action, int _aggressivity_action, int _defense_action) public onlyOwner{
        uint256 _tokenid = totalSupply();
        All_Cards.push(Cards({name : _name, aggressivity : _aggressivity, defense : _defense, quantity:_quantity, description:_description, Object_of_action:_Object_of_action, aggressivity_action:_aggressivity_action,defense_action:_defense_action, tokenid:_tokenid}));
        map_cards[_tokenid]=Cards({name : _name, aggressivity : _aggressivity, defense : _defense, quantity:_quantity, description:_description, Object_of_action:_Object_of_action, aggressivity_action:_aggressivity_action,defense_action:_defense_action, tokenid:_tokenid});
        _safeMint(owner(), _tokenid);
    }



    enum StateType {
      ItemAvailable,
      OfferPlaced,
      Accepted
    }

    string internal ApplicationName = "SimpleMarketplace";
    string internal WorkflowName = "SimpleMarketplace";

    event ContractCreated(string applicationName, string workflowName, address originatingAddress);
    event ContractUpdated(string applicationName, string workflowName, string action, address originatingAddress);

    // address[] public InstanceOwner;
    // Cards[] public SaleCards;
    // string public Description;
    // uint[] public AskingPrice;
    // StateType[] public State;
    // InstanceBuyer[] public InstanceBuyer;
    mapping (uint => Cards) SaleCards;
    mapping (uint => StateType) State;
    // uint[] public OfferPrice;
    struct InstanceOwner{
        address Owner_address;
        uint Card_mapping_key;
        uint AskingPrice;
        string Description;
    }
    struct InstanceBuyer{
        address Buyer_address;
        uint Card_mapping_key;
        uint offerPrize;
    }
    
    // InstanceOwnerInstanceBuyermapping
    mapping (uint => InstanceOwner) public AllInstanceOwner;
    mapping (uint => InstanceBuyer) public AllInstanceBuyer;

    //ERC20
    // Ethermon20 ERC_20;

    //ERC777
    ERC777Token ERC_777;


    function CreateDeal (uint Card_mapping_key, string memory description, uint price) public {
         InstanceOwner memory newOwner = InstanceOwner({Owner_address:msg.sender, Card_mapping_key:Card_mapping_key, AskingPrice:price, Description:description});
        // AllInstanceOwner[Card_mapping_key].Owner_address = msg.sender;
        // AllInstanceOwner[Card_mapping_key].Card_mapping_key = Card_mapping_key;
        // AllInstanceOwner[Card_mapping_key].AskingPrice = price;
        // AllInstanceOwner[Card_mapping_key].Description = description;
         AllInstanceOwner[Card_mapping_key]=newOwner;
        SaleCards[Card_mapping_key]=map_cards[Card_mapping_key];
        State[Card_mapping_key] = StateType.ItemAvailable;
        // emit ContractCreated(ApplicationName, WorkflowName, msg.sender);
    }

    function MakeOffer(uint Card_mapping_key, uint offerPrice) public {
        InstanceBuyer memory newBuyer;
        newBuyer.Buyer_address = msg.sender;
        newBuyer.Card_mapping_key = Card_mapping_key;
        newBuyer.offerPrize = offerPrice;
        if (offerPrice == 0) {
            revert("MakeOffer function needs to have an offerPrice > 0");
        }

        if (State[Card_mapping_key] != StateType.ItemAvailable) {
            revert("MakeOffer function can only be called when at ItemAvailable state");
        }

        if ((AllInstanceOwner[Card_mapping_key].Owner_address == msg.sender)) {
            revert("MakeOffer function cannot be called by the owner");
        }
        if(ERC_777.balanceOf(msg.sender) < AllInstanceOwner[Card_mapping_key].AskingPrice){
            revert("MakeOffer function cannot be called when the buyer doesn't have enough money");
        }
        AllInstanceBuyer[Card_mapping_key] = newBuyer;
        State[Card_mapping_key] = StateType.OfferPlaced;

        // emit ContractUpdated(ApplicationName, WorkflowName, "MakeOffer", msg.sender);
    }

    function Reject(uint Card_mapping_key, address ThisBuyer) public {
        if (State[Card_mapping_key] != StateType.OfferPlaced) {
            revert("Reject function can only be called when in OfferPlaced state");
        }

        if (AllInstanceOwner[Card_mapping_key].Owner_address != msg.sender) {
            revert("Reject function can only be called by the owner");
        }
        delete AllInstanceBuyer[Card_mapping_key];
        State[Card_mapping_key] = StateType.ItemAvailable;

        // emit ContractUpdated(ApplicationName, WorkflowName, "Reject", msg.sender);
    }

    function AcceptOffer(uint Card_mapping_key, address ThisBuyer) public {
        if (AllInstanceOwner[Card_mapping_key].Owner_address != msg.sender) {
            revert("AcceptOffer function can only be called by the owner");
        }
        //ERC20
        // REC_20.approve(AllInstanceBuyer[Card_mapping_key].Buyer_address, AllInstanceOwner[Card_mapping_key].AskingPrice);
        // ERC_20.transferFrom(AllInstanceBuyer[Card_mapping_key].Buyer_address,msg.sender,AllInstanceOwner[Card_mapping_key].AskingPrice);
        // ERC_20.safeTransferFrom(AllInstanceBuyer[Card_mapping_key].Buyer_address,msg.sender,AllInstanceOwner[Card_mapping_key].AskingPrice);

        //ERC777
        ERC_777.approve(AllInstanceBuyer[Card_mapping_key].Buyer_address, AllInstanceOwner[Card_mapping_key].AskingPrice);
        ERC_777.transferFrom(AllInstanceBuyer[Card_mapping_key].Buyer_address, msg.sender, AllInstanceOwner[Card_mapping_key].AskingPrice);
        approve(AllInstanceBuyer[Card_mapping_key].Buyer_address, Card_mapping_key);
        safeTransferFrom(msg.sender,AllInstanceBuyer[Card_mapping_key].Buyer_address,Card_mapping_key);
        State[Card_mapping_key] = StateType.Accepted;
        delete AllInstanceOwner[Card_mapping_key];
        delete AllInstanceBuyer[Card_mapping_key];
        delete SaleCards[Card_mapping_key];
        // emit ContractUpdated(ApplicationName, WorkflowName, "AcceptOffer", msg.sender);
    }
    // function FindOwnerByCardMappingKey(uint Card_mapping_key) public{
    //     int i=0;
    //     InstanceBuyer Owner = AllInstanceOwner[Card_mapping_key];
    //     return Owner;
    // }
}
